mapper.select:
SELECT COUNT(id) FROM tb_notice where id>0
AND province_id=#{filter.proId}
AND city_id LIKE #{filter.city_id}

mapper.update:
UPDATE tb_notice SET Title=#{entity.Title}, Content=#{entity.Content}, MessageDate=NOW(), mark=#{entity.mark}, is_top=#{entity.is_top}, author=#{entity.author},
province_id=#{entity.province_id},
city_id=#{entity.cityId},
area_id=#{entity.areaId}
WHERE Id = #{entity.Id}

mapper.select:
SELECT Id AS "Id", Title AS "Title", Content AS "Content", mark AS mark, is_top AS is_top, MessageDate AS "MessageDate", author AS "author" FROM tb_notice
where id > 0
AND province_id=#{filter.proId}
AND city_id LIKE #{filter.city_id}
ORDER BY id DESC
LIMIT #{filter.first},#{filter.last}

        SELECT
            Id AS "Id",
            Title AS "Title",
            Content AS "Content",
            mark AS mark,
            is_top AS is_top,
            DATE_FORMAT(MessageDate,'%M %D,%Y') AS "MessageDate",
            author AS "author"
        FROM
          tb_notice
          where 
          id>0

			and mark = #{filter.mark}


			and is_top = #{filter.is_top}


			and is_top != '1'


			AND province_id=#{filter.proId}



			AND city_id LIKE #{filter.city_id}


			AND area_id LIKE #{filter.area_id}



        ORDER BY id DESC  

			LIMIT	#{filter.first},#{filter.last}
